import React, { useState, useEffect } from 'react'
import Footer from "./Footer";
import {
  BrowserRouter as Router,
  Route,
  NavLink,
  Link,
} from 'react-router-dom'

import Container from "react-bootstrap/Container"

import { Divider } from 'antd'
import { GoNoNewline } from "react-icons/go";
import '@trussworks/react-uswds/lib/uswds.css'
import '@trussworks/react-uswds/lib/index.css'
import {
  GovBanner,
  Header,
  Title,
  NavMenuButton,
  PrimaryNav,
  GridContainer,
} from '@trussworks/react-uswds'

import HomePage from './pages/Home'
import ExamplePage from './pages/Example'
import FormsPage from './pages/Forms'
import IconsPage from './pages/Icons'
import { Routes } from './routes'

import './App.css'

const App = () => {
  const [mobileNavOpen, setMobileNavOpen] = useState(false)
  const { HOME_PAGE, EXAMPLES_PAGE, FORMS_PAGE, ICONS_PAGE } = Routes

  // #####################################################
  const [currentTime, setCurrentTime] = useState(0);

  useEffect(() => {
    fetch('/api/test').then(res => res.json()).then(data => {
      setCurrentTime(data.current_time);
      console.log(data.current_time);
    });
  }, []);
  // ######################################

  const toggleMobileNav = (): void => {
    setMobileNavOpen((prevOpen) => !prevOpen)
  }

  const navItems = [
    <NavLink to="/HOME_PAGE" >
      Home
    </NavLink>,
    <NavLink to="/EXAMPLES_PAGE">
      Examples
    </NavLink>,
    <NavLink to="/ICONS_PAGE">
      Icons
    </NavLink>,
  ]

  const Footer = (props) => {
    return (
      <div className="footer-copyright py-3 bg-light text-black-50">
        <Container className='d-flex justify-content-between px-5'>
          <span>Copyright &copy; {new Date().getFullYear()} Department for the Protection of Pets</span>
          <div>
            <a href="#" className="text-black-50 text-decoration-none p-1"> Privacy Policy</a>
            <Divider type="vertical" />
            <a href="#" className="text-black-50 text-decoration-none p-1"> Terms of Service</a>
            <Divider type="vertical" />
            <a href="#" className="text-black-50 text-decoration-none p-1"> Department of pets</a>
            {/* <Divider type="vertical" /> */}
            <a href="#" className="text-black-50 text-decoration-none p-1"> {props.currentTime}</a>
          </div>
        </Container>
      </div>
    );
  }

  return (

    <Router>
      <GovBanner />
      <Header basic>
        <div className="usa-nav-container">
          <div className="usa-navbar">
            <Title>
              <Link to={HOME_PAGE}>Department for the Protection of Pets</Link>
            </Title>
            <NavMenuButton
              label="Menu"
              onClick={toggleMobileNav}
              className="usa-menu-btn"
            />
          </div>

          <PrimaryNav
            aria-label="Primary navigation"
            items={navItems}
            onToggleMobileNav={toggleMobileNav}
            mobileExpanded={mobileNavOpen}
          />

          <Footer currentTime={currentTime} />
        </div>
      </Header>




    </Router>





  );
}

export default App
